Properties.Octopus.Action.Script.ScriptBody:
Import-Module WebAdministration

$applicationPoolName = $OctopusParameters["ApplicationPoolName"]
$idleTimeout = $OctopusParameters["IdleTimeoutMinutes"]
$periodicRestart = $OctopusParameters["RegularTimeIntervalMinutes"]
$periodicRecycleTime = $OctopusParameters["PeriodicRecycleTime"]
$recycleEventsToLog = $OctopusParameters["RecycleEventsToLog"]



function Update-IISAppPool-RecyclingTimeoutSettings($appPoolName, $idleTimeout, $periodicRestart) {
    Write-Output "Setting worker process idle timeout to $idleTimeout and periodic restart time to $periodicRestart for AppPool $appPoolName."
    $Pool = Get-Item IIS:\AppPools\$appPoolName
    $Pool.ProcessModel.IdleTimeout = [TimeSpan]::FromMinutes($idleTimeout)
    if(![string]::IsNullOrEmpty($periodicRestart)){
        $Pool.Recycling.PeriodicRestart.Time = [TimeSpan]::FromMinutes($periodicRestart)
    }
    $Pool | Set-Item
}

function Update-IISAppPool-Setting($appPoolName, $settingName, $value) {
    Write-Output "Setting $settingName for AppPool $appPoolName to $value."
    Clear-ItemProperty IIS:\AppPools\$appPoolName -Name $settingName # Clear any existing schedules
    Set-ItemProperty IIS:\AppPools\$appPoolName -Name $settingName -Value $value
}

Update-IISAppPool-RecyclingTimeoutSettings -AppPoolName $applicationPoolName -IdleTimeout $idleTimeout -PeriodicRestart $periodicRestart

if(![string]::IsNullOrEmpty($periodicRecycleTime)){
    Update-IISAppPool-Setting -AppPoolName $applicationPoolName -SettingName Recycling.periodicRestart.schedule -Value @{value=$periodicRecycleTime}    
}

if(![string]::IsNullOrEmpty($recycleEventsToLog)){
    Update-IISAppPool-Setting -AppPoolName $applicationPoolName -SettingName Recycling.logEventOnRecycle -Value $recycleEventsToLog    
}
